mapper.select:
select v.id as reportId, s.platform_session_room_id as platformSessionRoomId, s.start_time as startTime,
v.report_time as reportTime, v.prev_report_time as lastReportTime, v.diff_minutes as diffMinutes,
v.gmv_amount_delta as gmvAmount, v.deal_item_cnt_delta as dealItemCnt, v.refund_amount_delta as refundAmount, v.exposure_cnt_delta as exposureCnt, v.viewer_cnt_delta as viewerCnt, v.new_fans_cnt_delta as newFansCnt, v.deal_user_cnt_delta as dealUserCnt, v.qianchuan_cost_delta as qianchuanCost,
case when ifnull(v.viewer_cnt_delta,0) = 0 then 0 else round(v.deal_user_cnt_delta / v.viewer_cnt_delta, 6) end as conversionRate,
case when ifnull(v.exposure_cnt_delta,0) = 0 then 0 else round(v.viewer_cnt_delta / v.exposure_cnt_delta, 6) end as exposureWatchRate,
null as watchFollowRate
from v_live_live_owner_report_delta v join live_live_session s on s.id = v.session_id where 1 = 1 and v.owner_id = #{ownerId} and s.room_id = #{roomId}
and v.report_time >= #{beginTime}
and v.report_time <= #{endTime}
order by v.report_time desc

SELECT /* ===== 维度信息 ===== */ rm.room_name AS roomName, s.platform_session_room_id AS platformSessionRoomId, o.owner_name AS ownerName,
v.session_id AS sessionId, v.owner_id AS ownerId,
/* ===== 时间 ===== */ v.report_time AS reportTime, v.prev_report_time AS prevReportTime, v.diff_minutes AS diffMinutes,
/* ===== 累计（上报值） ===== */ v.exposure_cnt AS exposureCnt, v.viewer_cnt AS viewerCnt, v.new_fans_cnt AS newFansCnt, v.deal_user_cnt AS dealUserCnt, v.deal_item_cnt AS dealItemCnt, v.gmv_amount AS gmvAmount, v.refund_amount AS refundAmount, v.qianchuan_cost AS qianchuanCost,
/* ===== 本段 Δ ===== */ v.exposure_cnt_delta AS exposureCntDelta, v.viewer_cnt_delta AS viewerCntDelta, v.new_fans_cnt_delta AS newFansCntDelta, v.deal_user_cnt_delta AS dealUserCntDelta, v.deal_item_cnt_delta AS dealItemCntDelta, v.gmv_amount_delta AS gmvAmountDelta, v.refund_amount_delta AS refundAmountDelta, v.qianchuan_cost_delta AS qianchuanCostDelta,
/* ===== 上报比率（累计上报值，来自视图/或原表拼进视图） ===== */ v.exposure_watch_rate AS exposureWatchRate, v.watch_follow_rate AS watchFollowRate,
/* ===== 本段 Δ 比率（用 Δ 的分子分母算） ===== */ CASE WHEN IFNULL(v.exposure_cnt_delta, 0) = 0 THEN 0 ELSE ROUND(v.viewer_cnt_delta / v.exposure_cnt_delta, 6) END AS exposureWatchRateDelta,
CASE WHEN IFNULL(v.viewer_cnt_delta, 0) = 0 THEN 0 ELSE ROUND(v.new_fans_cnt_delta / v.viewer_cnt_delta, 6) END AS watchFollowRateDelta,
CASE WHEN IFNULL(v.viewer_cnt, 0) = 0 THEN 0 ELSE ROUND(v.deal_user_cnt / v.viewer_cnt, 6) END AS conversionRate,
CASE WHEN IFNULL(v.exposure_cnt, 0) = 0 THEN 0 ELSE ROUND(v.viewer_cnt / v.exposure_cnt, 6) END AS exposureClickRate,
CASE WHEN IFNULL(v.viewer_cnt_delta, 0) = 0 THEN 0 ELSE ROUND(v.deal_user_cnt_delta / v.viewer_cnt_delta, 6) END AS conversionRateDelta,
CASE WHEN IFNULL(v.exposure_cnt_delta, 0) = 0 THEN 0 ELSE ROUND(v.viewer_cnt_delta / v.exposure_cnt_delta, 6) END AS exposureClickRateDelta
FROM v_live_live_owner_report_delta v JOIN live_live_session s ON s.id = v.session_id JOIN live_live_room rm ON rm.id = s.room_id LEFT JOIN live_live_owner o ON o.id = v.owner_id WHERE 1 = 1
AND v.report_time >= #{beginTime}
AND v.report_time <= #{endTime}
AND rm.room_name LIKE CONCAT('%', #{roomName}, '%')
AND o.owner_name LIKE CONCAT('%', #{ownerName}, '%')
ORDER BY rm.id ASC, v.session_id ASC, v.owner_id ASC, v.report_time DESC
