mapper.update:
UPDATE live_user
SET status = #{status}
WHERE id = #{id}

mapper.select:
SELECT
id,
room_id AS roomId,
username,
status,
create_time AS createTime
FROM live_user

    room_id = #{req.roomId}


        AND username LIKE CONCAT('%', #{req.username}, '%')



        AND status = #{req.status}



        AND create_time >= #{req.beginTime}



        AND create_time <= #{req.endTime}


ORDER BY create_time DESC, id DESC

SELECT r.name AS roomName, u.username AS username, u.status AS status, u.create_time AS userCreateTime FROM live_user u JOIN live_room r ON r.id = u.room_id WHERE u.room_id IN
#{id}
ORDER BY r.id DESC, u.id DESC

SELECT r.name AS roomName, u.username AS username, c.content AS content, c.create_time AS commentTime FROM live_comment c JOIN live_user u ON u.id = c.user_id JOIN live_room r ON r.id = c.room_id WHERE c.room_id = #{req.roomId}
AND u.username LIKE CONCAT('%', #{req.username}, '%')
AND u.status = #{req.status}
AND u.create_time >= #{req.beginTime}
AND u.create_time <= #{req.endTime}
ORDER BY c.id DESC
